import type { ERC7579Validator, UserOp } from '@/core'
import { SendopError } from '@/error'

type ConstructorOptions = {
	address: string
}

export class WebAuthnValidator implements ERC7579Validator {
	#address: string

	constructor(options: ConstructorOptions) {
		this.#address = options.address
	}

	address() {
		return this.#address
	}

	getDummySignature(userOp: UserOp) {
		return ''
	}

	async getSignature(userOpHash: Uint8Array, userOp: UserOp) {
		const signature = prompt('Please input the signature')
		if (!signature) {
			throw new WebAuthnValidatorError('Signature is required in getSignature')
		}
		return signature
	}

	async getAccounts(): Promise<string[]> {
		// TODO: signer needs to have webauthn public key
		return []
	}
}

export class WebAuthnValidatorError extends SendopError {
	constructor(message: string, cause?: Error) {
		super(message, cause)
		this.name = 'WebAuthnValidatorError'
	}
}
